import './App.css';
import ArmLion from './images/ArmLion.png'
import ArmEagle from './images/ArmEagle.png'
import Fade from 'react-reveal/Fade';
import React, { useState } from 'react'
import Zoom from 'react-reveal/Zoom';
import HeadShake from 'react-reveal/HeadShake';


function App() {
  const [showCoat, setShowCoat] = useState(false)
  const [coatShake, setCoatShake] = useState(false)
  return (
    <Fade duration={2000} wait={1500} onReveal={ () => {
      setShowCoat(true)
      setTimeout(() => {
        setCoatShake(true)
      }, 2200);
    }}>
    <div className="mainCont">

    <Zoom left when={showCoat}  duration={2000} >
    <HeadShake when={coatShake}>
      <div style={{ width:'250px', height:'500px',}}>
        <img style={{ width:'100%', height:'100%'}} src={ArmEagle} alt="" />
      </div>
      </HeadShake>
      </Zoom>

      <Zoom right when={showCoat} duration={2000}>
      <HeadShake when={coatShake}>
      <div style={{ width:'250px', height:'500px'}}>
        <img style={{width:'100%', height:'100%'}} src={ArmLion} alt="" />
      </div>
   </HeadShake>
      </Zoom>

    </div>
    </Fade>
  );
}

export default App;
