import './App.css';
import ArmLion from './images/ArmLion.png'
import ArmEagle from './images/ArmEagle.png'
import EagleLion from './images/Eagle&Lion.png'
import SilverShield from './images/SilverShield.png'
import Fade from 'react-reveal/Fade';
import React, { useState } from 'react'
import Zoom from 'react-reveal/Zoom';
import Jump from 'react-reveal/Jump';
import brightKnight from './images/brightKnight0.png'
import darkKnight from './images/darkKnight0.png'


function App() {
  const [showCoat, setShowCoat] = useState(false)
  const [beginFuse, setBeginFuse] = useState('2')
  const [showFuse, setShowFuse] = useState('0')
  const [showFused, setShowFused] = useState('0')
  const [coatJump, setCoatJump] = useState(false)

const fuse = {
  boxShadow: `0 0 40px 5px #fff,
  inset  0 0 20px #fff,
    0 0 160px 90px #08f,
    inset 0 0 20px #08f,
    0 0 320px 50px #08f,
    inset 0 0 20px #08f`,
    position:'absolute', 
    top:'27%',
    zIndex:'10',
    opacity:showFuse,
    height:beginFuse

}

  const [leftPosL, setLeftPosL] = useState('0px');
  const [leftPosR, setLeftPosR] = useState('194px');
  const [releaseHorses, setReleaseHorses] = useState(false)

  return (


    <Fade duration={2000} wait={1500} onReveal={ () => setReleaseHorses(true)}>
    <div className="mainCont" onClick={()=>{
      // setLeftPosL('0px');
      // setLeftPosR('194px')
    }}>


      <div style={{position:'relative' }}>

        <Zoom when={releaseHorses}>
          <img className='knights' style={{left:leftPosL}} src={brightKnight} alt="" />
        </Zoom>
        <Zoom when={releaseHorses}>
          <img className='knights' style={{left:leftPosR,transform:'rotateY(180deg) '}} 
          src={darkKnight} alt="" />
        </Zoom>



        {/* <img style={{position:'absolute', top:'0',left:'0',opacity:showFused,transition:'all 3s'}}
         src={EagleLion} alt="" /> */}
        </div>


    </div>
    </Fade>

  );
        }

export default App;

